script:
frappe.ui.form.on('Lead', {
	refresh(frm) {
		// your code here

	},
	province(frm) {
        if (frm.doc.province) {
            frappe.db.get_doc('Province', frm.doc.province).then(doc => {
                frm.set_value('region', doc.region);
            });
        }
    },
	region(frm){
	    refresh_province(frm)
	},
    refresh_province(frm){
        frm.set_query("province", function() {
            if (frm.doc.region) {
                return {
                    filters: {
                        region: frm.doc.region
                    }
                };
            }
        });
    }
})
